using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Library
{
    public class UtilityFunctions
    {
        //An additional class for all utility functions that are not directly related to any specific entity.
        public static void PrintInBox(string text )
        {
            //This method was generated by ChatGPT


            int width = 100; // Total width of the box including borders
            int padding = (width - text.Length - 2) / 2; // Padding to center the text

            // Ensure width is sufficient to contain the text with borders
            if (width < text.Length + 2)
            {
                width = text.Length + 4; // Adjust width if necessary
                padding = (width - text.Length - 2) / 2;
            }

            Console.ForegroundColor = ConsoleColor.Yellow;

            // Top border
            Console.WriteLine(new string('=', width));

            // Text line with padding
            Console.Write("||");
            Console.Write(new string(' ', padding));
            Console.Write(text);
            Console.Write(new string(' ', width - text.Length - padding - 4));
            Console.WriteLine("||");

            // Bottom border
            Console.WriteLine(new string('=', width));

            // Reset colors
            Console.ResetColor();
        }

        public static void PrintGreen(string text)
        {
            Console.ForegroundColor = ConsoleColor.Green;
            Console.WriteLine(text);
            Console.ResetColor();
        }

        public static void PrintPosts(List<Post> posts)
        {
            foreach (var post in posts)
            {
                Console.WriteLine(post.ToString());
            }
        }

        public static void PrintTags(List<Tag> tags)
        {
            foreach (var tag in tags)
            {
                Console.WriteLine(tag.ToString());
            }
        }


    }
}
